function (keys,values,rereduce) {
	var maxlen=5;
	// { "genre": {"count": .. , "artists": [ "artist","artist","artist"] }
	if ( !rereduce ) {
		var back = { count: 0, artists: [] };
		for ( var k in keys ) {
			var genre = keys[k][0][0];
			var artist = keys[k][0][1];
			back.count ++;
			if ( back.artists.length < maxlen && back.artists.indexOf(artist) < 0 ) {
				back.artists.push(artist);
			}
		}
		return back;
	}
	var back = values.pop();
	for ( var v in values ) {
		back.count += values[v].count;
		for ( var a in values[v].artists ) {
			if ( back.artists.count < maxlen && back.artists.indexOf(values[v].artists[a]) < 0 ) {
				back.artists.push(values[v].artists[a]);
			}
		}
	}
	return back;
}

