songs.map:
function (doc) {
  if ( doc._id.substring(0,2) == 'aa' ) {
    emit (doc._id, null);
  } else if ( doc._id.substr(0,2) == "pr" && doc.listen ) {
    for ( var i in doc.listen ) {
       emit(i,null);
    }
  } else if ( doc._id.substr(0,2) == "up" ) {
    if ( doc.likes ) {
      for ( var i in doc.likes ) {
        emit(i,null);
      }
    }
    if ( doc.dislikes ) {
      for ( var i in doc.dislikes ) {
        emit(i,null);
      }
    }
    if ( doc.playlist && doc.playlist.list ) {
      for ( var i in doc.playlist.list ) {
        emit(doc.playlist.list[i],null);
      }
    }
  }
}

name.map:
function (doc) {
  if ( doc._id.substring(0,2) == 'up' && doc.likes ) {
	for ( var index in doc.likes ) {
	    emit ([doc._id.replace(/^up/,'us'), index], index );
	}
  }
}
